import React from 'react';
import styled from 'styled-components';
import { FormGroup,Form, Row, Col, FormLabel, FormControl, FloatingLabel, Button, InputGroup, FormSelect, FormCheck} from "react-bootstrap";
import {Formik} from "formik";
import * as Yup from "yup";
// Styled-components styles


const MYFORM = styled(Form)`
  width: 90%;
  text-align: left;
  padding-top: 2em;
  padding-bottom: 2em;

  @media(min-width: 786px) {
    width: 50%;
  }
`;

const BUTTON = styled(Button)`
  background: #1863AB;
  border: none;
  font-size: 1.2em;
  font-weight: 400;

  &:hover {
    background: #1D3461;
  }
`;

const Formularz = () => {
    const phoneRegExp = /^(\+?\d{0,4})?\s?-?\s?(\(?\d{3}\)?)\s?-?\s?(\(?\d{3}\)?)\s?-?\s?(\(?\d{4}\)?)?$/;

    const validationSchema = Yup.object().shape({
        name: Yup.string()
            .min(2, "*Names must have at least 2 characters")
            .max(8, "*Names can't be longer than 100 characters")
            .required("*Name is required"),
        lastName: Yup.string()
            .min(2, "*Last name must have at least 2 characters")
            .max(8, "*Last name can't be longer than 100 characters")
            .required("*Last name is required"),
        email: Yup.string()
            .email("*Must be a valid email address")
            .max(8, "*Email must be less than 100 characters")
            .required("*Email is required"),
        phone: Yup.string()
            .matches(phoneRegExp, "*Phone number is not valid")
            .required("*Phone number required"),
        blog: Yup.string()
            .url("*Must enter URL in http://www.example.com format")
            .required("*URL required")
    });
    return (
        <div className="container1">
            <Formik initialValues={{ name:"", lastName: "", email:"", phone:"", blog:""}}
            validationSchema={validationSchema}
                    onSubmit={(values, {setSubmitting, resetForm}) => {
                        setSubmitting(true);
                        setTimeout(() => {
                            alert(JSON.stringify(values, null, 2));
                            resetForm();
                            setSubmitting(false);
                        }, 500);
                    }}
            >
                {( {values,
                       errors,
                       touched,
                       handleChange,
                       handleBlur,
                       handleSubmit,
                       isSubmitting }) =>(
                        <Form className="mx-auto" onSubmit={handleSubmit}>
                            {console.log(values)}
                            <Row className="mb-3">
                                <Form.Group as={Col} controlId="formName">
                                    <Form.Label style={{color: "blue",
                                        textTransform: "uppercase",
                                        marginBottom: "10px",
                                        marginRight: "10px"
                                    }}>ImiÄ™ :</Form.Label>
                                    <Form.Control
                                        type="text"
                                        name="name"
                                        placeholder="Full Name"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.name}
                                        className={touched.name && errors.name ? "error" : null}
                                    />
                                    {touched.name && errors.name ? (
                                        <div className="error-message">{errors.name}</div>
                                    ) : null}
                                </Form.Group>
                                <Form.Group controlId="formlastName" as={Col}>
                                    <Form.Label style={{color: "blue",
                                        textTransform: "uppercase",
                                        marginBottom: "10px",
                                        marginRight: "10px"
                                    }}>Nazwisko :</Form.Label>
                                    <Form.Control
                                        type="text"
                                        name="lastName"
                                        placeholder="Nazwisko"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.lastName}
                                        className={touched.lastName && errors.lastName ? "error" : null}
                                    />
                                    {touched.lastName && errors.lastName ? (
                                        <div className="error-message">{errors.lastName}</div>
                                    ) : null}
                                </Form.Group>
                            </Row>
                            <Row className="mb-3">
                                <Form.Group as={Col} controlId="formEmail">
                                    <Form.Label>Email :</Form.Label>
                                    <Form.Control
                                        type="text"
                                        name="email"
                                        placeholder="Email"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.email}
                                        className={touched.email && errors.email ? "error" : null}
                                    />{touched.email && errors.email ? (
                                    <div className="error-message">{errors.email}</div>
                                ) : null}
                                </Form.Group>
                                <Form.Group as={Col} controlId="formPhone">
                                    <Form.Label>Numer telefonu :</Form.Label>
                                    <Form.Control
                                        type="text"
                                        name="phone"
                                        placeholder="Numer telefonu"
                                        onChange={handleChange}
                                        onBlur={handleBlur}
                                        value={values.phone}
                                        className={touched.phone && errors.phone ? "error" : null}
                                    />
                                </Form.Group>

                            </Row>



                            <Form.Group controlId="formBlog">
                                <Form.Label>Blog :</Form.Label>
                                <Form.Control
                                    type="text"
                                    name="blog"
                                    placeholder="Blog URL"
                                    onChange={handleChange}
                                    onBlur={handleBlur}
                                    value={values.blog}
                                />
                            </Form.Group>
                            <BUTTON variant="primary" type="submit" disabled={isSubmitting}>
                                Submit
                            </BUTTON>
                        </Form>
                    )}

            </Formik>
        </div>
    );
}


export default Formularz;
